mapper.update:
update tbl_post
set post_view_count = post_view_count + 1
where id = #{id}

update tbl_vt
set now_recruitment_count = tbl_vt.now_recruitment_count + 1
where id = #{vtId}

update tbl_vt
set id = #{id},
    recruitment_count = #{recruitmentCount},
    vt_s_date = #{vtSDate},
    vt_e_date = #{vtEDate}
WHERE ID = #{id}
