mapper.select:
select v.id, v.recruitment_count, p.post_title, m.member_name, p.post_status, pf.profile_file_name, pf.profile_file_path, pf.profile_file_size, pf.profile_file_type, v.vt_s_date, v.vt_e_date, p.post_view_count, p.post_type, p.post_summary, p.created_date from tbl_vt v left join tbl_post p on v.id = p.id left join tbl_member m on p.member_id = m.id left join tbl_profile pf on p.member_id = pf.id
p.post_type = #{pagination.postType}
and v.vt_e_date >= curdate()
order by
p.created_date desc, v.id p.post_view_count desc, p.created_date desc, v.id v.vt_e_date asc, p.created_date desc, v.id p.created_date desc, v.id
limit #{pagination.startRow}, #{pagination.rowCount}

mapper.update:
update tbl_post
set post_view_count = post_view_count + 1
where id = #{id}

update tbl_vt
set  now_recruitment_count = tbl_vt.now_recruitment_count + 1
where id = #{id}

update tbl_vt
set id = #{id},
    recruitment_count = #{recruitmentCount},
    vt_s_date = #{vtSDate},
    vt_e_date = #{vtEDate}
WHERE ID = #{id}
