mapper.update:
UPDATE tbl_vt_application
SET
    application_status = #{applicationStatus},
    vt_id = #{vtId},
    member_id = #{memberId}
WHERE id = #{id}

UPDATE tbl_vt_application
SET application_status = #{status}
WHERE id = #{applicationId}

mapper.select:
SELECT member_id
FROM tbl_vt_application
WHERE id = #{applicationId}
